import axios from "axios";
import { useCallback, useState } from "react";
import { Employee } from "../types/api/Employee";
import { useMessage } from "./useMessage";
import {
  EmployeeNameState,
  EmployeeFullNameState,
  EmployeeHobbiesState,
  EmployeeImageState,
  EmployeeState,
} from "../contexts/EmployeeState";
import { useRecoilState } from "recoil";
import { useNavigate } from "react-router-dom";

type Props = {
  imageFile: any;
  employeeId: string;
  employeeName: string;
  employeeFullName: string;
  hobbies: string;
};

export const useEditProfile = () => {
  const navigate = useNavigate();
  const { showMessage } = useMessage();
  const [loading, setLoading] = useState(false);
  const [employeeImageState, setEmployeeImageState] =
    useRecoilState(EmployeeImageState);
  const [employeeNameState, setEmployeeNameState] =
    useRecoilState(EmployeeNameState);
  const [employeeFullNameState, setEmployeeFullNameState] = useRecoilState(
    EmployeeFullNameState
  );
  const [employeeHobbiesState, setEmployeeHobbiesState] =
    useRecoilState(EmployeeHobbiesState);

  const updateProfile = useCallback(async (props: Props) => {
    const { imageFile, employeeId, employeeName, employeeFullName, hobbies } =
      props;
    const formData = new FormData();
    formData.append("image", imageFile);
    formData.append("employeeId", employeeId);
    formData.append("employeeName", employeeName);
    formData.append("employeeFullName", employeeFullName);
    formData.append("hobbies", hobbies);

    await axios
      .post("/api/editProfile", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
      .then((res) => {
        setEmployeeImageState(res.data.image);
        setEmployeeNameState(res.data.employeeName);
        setEmployeeFullNameState(res.data.employeeFullName);
        setEmployeeHobbiesState(res.data.hobbies);
        showMessage({ title: "更新が完了しました", status: "success" });
        navigate("/home/setting");
      })
      .catch(() => {
        showMessage({ title: "更新に失敗しました", status: "error" });
      });
  }, []);

  return { updateProfile, loading };
};
