import { useCallback, useEffect } from "react";
import { FC, memo } from "react";
import {
  Center,
  Spinner,
  Wrap,
  WrapItem,
  useDisclosure,
} from "@chakra-ui/react";

import { EmployeeCard } from "../organisms/employee/EmployeeCard";
import { useAllEmployees } from "../../hooks/useAllEmployees";
import { useSelectEmployee } from "../../hooks/useSelectEmployee";
import { HeaderOnly } from "../templates/layout/HeaderOnly";
import { EmployeeDetailModal } from "../organisms/employee/EmployeeDetailModal";
import { useRecoilState } from "recoil";
import {
  EmployeeAdminState,
  EmployeeState,
} from "../../contexts/EmployeeState";

export const EmployeeInfoList: FC = memo(() => {
  const { isOpen, onClose, onOpen } = useDisclosure();
  const { getEmployees, employees, loading } = useAllEmployees();
  const { onSelectEmployee, selectedEmployee } = useSelectEmployee();
  const [employeeState, setEmployeeState] = useRecoilState(EmployeeState);
  const [employeeAdminState, setEmployeeAdminState] =
    useRecoilState(EmployeeAdminState);

  useEffect(() => {
    getEmployees();
  }, []);

  const onClickEmployee = useCallback(
    (id: string) => {
      onSelectEmployee({ id, employees, onOpen });
    },
    [employees]
  );

  return (
    <>
      <HeaderOnly children={""} />
      {loading ? (
        <Center>
          <Spinner />
        </Center>
      ) : (
        <Wrap p={{ base: 4, md: 10 }}>
          {employees.map((employee) => (
            <WrapItem key={employee.employeeId} mx={"auto"}>
              <EmployeeCard
                id={employee.employeeId}
                image={employee.image}
                employeeName={employee.employeeName}
                fullName={employee.employeeFullName}
                onClick={onClickEmployee}
              />
            </WrapItem>
          ))}
        </Wrap>
      )}
      <EmployeeDetailModal
        employee={selectedEmployee}
        isOpen={isOpen}
        onClose={onClose}
        id={employeeState}
        isAdmin={employeeAdminState}
      />
    </>
  );
});
