import { useToast } from "@chakra-ui/toast";
import { useCallback } from "react";

type Props = {
  title: string;
  status: "info" | "warning" | "success" | "error";
};

export const useMessage = () => {
  const toast = useToast();

  const showMessage = useCallback(
    (props: Props) => {
      const { title, status } = props;
      toast({
        position: "top",
        duration: 2000,
        isClosable: true,
        title,
        status,
      });
    },
    [toast]
  );

  return { showMessage };
};
