import { ChangeEvent, FC, FormEvent, memo, useCallback, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  Box,
  Button,
  Divider,
  Flex,
  Heading,
  Input,
  Stack,
} from "@chakra-ui/react";

import { useMessage } from "../../hooks/useMessage";

export const Login: FC = memo(() => {
  const navigate = useNavigate();
  const { showMessage } = useMessage();
  const [loading, setLoading] = useState(false);
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");

  const onChangeUserId = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setUserId(e.target.value),
    [userId]
  );
  const onChangePassword = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setPassword(e.target.value),
    [password]
  );

  //API送信関数
  const handleSubmit = async (e: FormEvent) => {
    console.log("ログインボタン押下");
    console.log(userId);
    console.log(password);
    setLoading(true);
    e.preventDefault();
    await axios
      .post("/api/login", {
        userId: userId,
        password: password,
      })
      .then((res) => {
        //ログイン
        showMessage({ title: "ログインしました", status: "info" });
        navigate(`/employeeInfoList/${userId}`);
      })
      .catch(() => {
        showMessage({ title: "ユーザーが見つかりません", status: "error" });
        setLoading(false);
      })
      .finally(() => {});
  };

  return (
    <Flex align={"center"} justify={"center"} height={"100vh"}>
      <Box bg={"white"} w={"sm"} p={4} borderRadius={"md"} shadow={"md"}>
        <Heading as={"h1"} size={"lg"} textAlign={"center"}>
          従業員プロフィール閲覧アプリ
        </Heading>
        <Divider my={4} />
          <Stack spacing={6} py={10} px={10}>
            <Input
              placeholder="ユーザID"
              type="text"
              value={userId}
              name="userId"
              onChange={onChangeUserId}
            />
            <Input
              placeholder="パスワード"
              type="password"
              value={password}
              name="password"
              onChange={onChangePassword}
            />
            <Button
              bg={"teal.400"}
              color={"white"}
              _hover={{ opacity: 0.8 }}
              isDisabled={userId === "" || password === ""}
              isLoading={loading}
              onClick={handleSubmit}
            >
              ログイン
            </Button>
          </Stack>
      </Box>
    </Flex>
  );
});
