import { ChangeEvent, FormEvent, SetStateAction, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

export const LoginAdmin: React.FC = () => {
  console.log("LoginAdmin");
  const navigate = useNavigate();
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");
  const [statusNgFlag, setStatusNgFlag] = useState(false);

  const onChangeUserId = (e:React.ChangeEvent<HTMLInputElement>) => setUserId(e.target.value);
  const onChangePassword = (e:React.ChangeEvent<HTMLInputElement>) => setPassword(e.target.value);

  //API送信関数
  const handleSubmit = async (e:React.FormEvent) => {
    e.preventDefault();
    console.log("ログインボタンがクリックされました");
    try {
      console.log("APIを送信します");
      const responce = await axios.post("/api/loginAdmin", {
        userId: userId,
        password: password,
      });
      console.log(responce);

      if (responce.status === 200) {
        console.log("管理者一覧画面に遷移します");
        navigate(`/adminInfoList/${userId}`);
      } else {
        setStatusNgFlag(true);
      }
    } catch (e) {
      console.log("Error:", e);
    }
  };

  return (
    <div>
      <h1>管理者ログイン画面</h1>
      {statusNgFlag && <span style={{color:"red"}}>ユーザIDまたはパスワードが間違っています</span>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="ユーザID"
          name="userId"
          value={userId}
          onChange={onChangeUserId}
        />
        <br />
        <input
          type="password"
          placeholder="パスワード"
          name="password"
          value={password}
          onChange={onChangePassword}
        />
        <button type="submit">ログイン</button>
      </form>
      <Link to="/">TOPへ戻る</Link>
    </div>
  );
};
