import { ChangeEvent, FC, FormEvent, memo, useCallback, useState } from "react";
import { HeaderOnly } from "../templates/layout/HeaderOnly";
import {
  Box,
  Divider,
  Flex,
  Heading,
  Input,
  Stack,
  Image,
  ButtonGroup,
} from "@chakra-ui/react";
import { PrimaryButton } from "../atoms/button/PrimaryButton";
import { useMessage } from "../../hooks/useMessage";
import { useRecoilState } from "recoil";
import {
  EmployeeState,
  EmployeeNameState,
  EmployeeFullNameState,
  EmployeeHobbiesState,
  EmployeeImageState,
} from "../../contexts/EmployeeState";
import { useNavigate } from "react-router-dom";

export const EditProfile: FC = memo(() => {
  const navigate = useNavigate();
  const { showMessage } = useMessage();
  const [loading, setLoading] = useState(false);
  const [employeeState, setEmployeeState] = useRecoilState(EmployeeState);
  const [employeeNameState, setEmployeeNameState] =
    useRecoilState(EmployeeNameState);
  const [employeeFullNameState, setEmployeeFullNameState] = useRecoilState(
    EmployeeFullNameState
  );
  const [employeeHobbiesState, setEmployeeHobbiesState] =
    useRecoilState(EmployeeHobbiesState);
  const [employeeImageState, setEmployeeImageState] =
    useRecoilState(EmployeeImageState);

  const onChangeEmployeeName = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setEmployeeNameState(e.target.value),
    [employeeNameState]
  );

  const onChangeEmployeeFullName = useCallback(
    (e: ChangeEvent<HTMLInputElement>) =>
      setEmployeeFullNameState(e.target.value),
    [employeeFullNameState]
  );

  const onChangeHobbies = useCallback(
    (e: ChangeEvent<HTMLInputElement>) =>
      setEmployeeHobbiesState(e.target.value),
    [employeeHobbiesState]
  );

  const handleCancelButton = () => {
    navigate("/home/setting");
  };

  function handleUpdateButton(e: FormEvent<Element>): Promise<void> {
    throw new Error("Function not implemented.");
  }

  return (
    <HeaderOnly>
      <Flex align={"center"} justify={"center"} height={"120vh"}>
        <Box bg={"white"} w={"sm"} p={4} borderRadius={"md"} shadow={"md"}>
          <Heading as={"h1"} size={"lg"} textAlign={"center"}>
            プロフィール編集
          </Heading>
          <Divider my={4} />
          <Stack spacing={6} py={10} px={10}>
            <Image
              borderRadius={"full"}
              boxSize={"160px"}
              src={`data:image/jpeg;base64,${employeeImageState}`}
              alt="画像"
              m={"auto"}
              bg={"gray.100"}
              textAlign={"center"}
            />
            <Input
              placeholder="社員ID"
              type="text"
              value={employeeState}
              name="employeeId"
              readOnly={true}
            />
            <Input
              placeholder="ニックネーム"
              type="text"
              value={employeeNameState}
              name="employeeName"
              onChange={onChangeEmployeeName}
            />
            <Input
              placeholder="フルネーム"
              type="text"
              value={employeeFullNameState}
              name="employeeFullName"
              onChange={onChangeEmployeeFullName}
            />
            <Input
              placeholder="趣味"
              type="text"
              value={employeeHobbiesState}
              name="hobbies"
              onChange={onChangeHobbies}
            />
            <Flex>
              <ButtonGroup gap="2" spacing="20">
                <PrimaryButton loading={loading} onClick={handleCancelButton}>
                  キャンセル
                </PrimaryButton>
                <PrimaryButton loading={loading} onClick={handleUpdateButton}>
                  更新
                </PrimaryButton>
              </ButtonGroup>
            </Flex>
          </Stack>
        </Box>
      </Flex>
    </HeaderOnly>
  );
});
