import { atom } from "recoil";

export const EmployeeState = atom({
  key: "employeeId",
  default: "",
});

export const EmployeeAdminState = atom({
  key: "isAdmin",
  default: 0,
});

export const EmployeeNameState = atom({
  key: "employeeName",
  default: "",
});

export const EmployeeFullNameState = atom({
  key: "employeeFullName",
  default: "",
});

export const EmployeeHobbiesState = atom({
  key: "hobbies",
  default: "",
});

export const EmployeeImageState = atom({
  key: "image",
  default: null,
});
