import { ChangeEvent, FormEvent, SetStateAction, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

export const LoginAdmin: React.FC = () => {
  console.log("LoginAdmin");
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");

  const onChangeUserId = (e: { target: { value: SetStateAction<string> } }) =>
    setUserId(e.target.value);
  const onChangePassword = (e: { target: { value: SetStateAction<string> } }) =>
    setPassword(e.target.value);

  const handleSubmit = async () => {
    console.log("ログインボタンがクリックされました");
    try {
      console.log("APIを送信します");
      const responce = await axios.post("/api/posts", {
        userId: userId,
        password: password,
      });
      console.log(responce);
    } catch (e) {
      console.log("Error:", e);
    }
  };

  return (
    <div>
      <h1>管理者ログイン画面</h1>
      <input
        type="text"
        placeholder="ユーザID"
        name="userId"
        value={userId}
        onChange={onChangeUserId}
      />
      <br />
      <input
        type="password"
        placeholder="パスワード"
        name="password"
        value={password}
        onChange={onChangePassword}
      />
      <button onClick={handleSubmit}>ログイン</button>
      <Link to="/">TOPへ戻る</Link>
    </div>
  );
};
