import { ChangeEvent, FC, FormEvent, memo, useCallback, useState } from "react";
import { HeaderOnly } from "../templates/layout/HeaderOnly";
import { Box, Divider, Flex, Heading, Input, Stack } from "@chakra-ui/react";
import { PrimaryButton } from "../atoms/button/PrimaryButton";
import { EmployeeState } from "../../contexts/EmployeeState";
import { useRecoilState } from "recoil";
import { useEditPassword } from "../../hooks/useEditPassword";

export const EditPassword: FC = memo(() => {
  const { editPassword, loading } = useEditPassword();
  const [employeeState, setEmployeeState] = useRecoilState(EmployeeState);
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");

  const onChangeOldPassword = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setOldPassword(e.target.value),
    [oldPassword]
  );

  const onChangeNewPassword = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setNewPassword(e.target.value),
    [newPassword]
  );

  return (
    <HeaderOnly>
      <Flex align={"center"} justify={"center"} height={"100vh"}>
        <Box bg={"white"} w={"sm"} p={4} borderRadius={"md"} shadow={"md"}>
          <Heading as={"h1"} size={"lg"} textAlign={"center"}>
            パスワード再設定
          </Heading>
          <Divider my={4} />
          <Stack spacing={6} py={10} px={10}>
            <Input
              placeholder="社員ID"
              type="text"
              value={employeeState}
              name="employeeId"
              readOnly={true}
            />
            <Input
              placeholder="旧パスワード"
              type="password"
              value={oldPassword}
              name="oldPassword"
              onChange={onChangeOldPassword}
            />
            <Input
              placeholder="新規パスワード"
              type="password"
              value={newPassword}
              name="newPassword"
              onChange={onChangeNewPassword}
            />
            <PrimaryButton
              disabled={oldPassword === "" || newPassword === ""}
              loading={loading}
              onClick={editPassword}
            >
              再設定
            </PrimaryButton>
          </Stack>
        </Box>
      </Flex>
    </HeaderOnly>
  );
});
