import { FC, memo, useState } from "react";
import { HeaderOnly } from "../templates/layout/HeaderOnly";
import { Box, Divider, Flex, Heading, Input, Stack } from "@chakra-ui/react";
import { PrimaryButton } from "../atoms/button/PrimaryButton";
import { EmployeeState } from "../../contexts/EmployeeState";
import { useRecoilState } from "recoil";

export const EditPassword: FC = memo(() => {
    const [employeeState, setEmployeeState] = useRecoilState(EmployeeState);
    const [oldPassword, setOldPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
  return (
    <HeaderOnly>
      <Flex align={"center"} justify={"center"} height={"100vh"}>
      <Box bg={"white"} w={"sm"} p={4} borderRadius={"md"} shadow={"md"}>
        <Heading as={"h1"} size={"lg"} textAlign={"center"}>
          パスワード再設定
        </Heading>
        <Divider my={4} />
        <Stack spacing={6} py={10} px={10}>
          <Input
            placeholder="社員ID"
            type="text"
            value={employeeState}
            name="employeeId"
            readOnly={true}
          />
          <Input
            placeholder="パスワード"
            type="password"
            value={password}
            name="password"
            onChange={onChangePassword}
          />
          <PrimaryButton
            disabled={employeeId === "" || password === ""}
            loading={loading}
            onClick={handleSubmit}
          >
            ログイン
          </PrimaryButton>
        </Stack>
      </Box>
    </Flex>
    </HeaderOnly>
  );
});
