import { ChangeEvent, FC, FormEvent, memo, useCallback, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  Box,
  Button,
  Divider,
  Flex,
  Heading,
  Input,
  Stack,
} from "@chakra-ui/react";

import { useMessage } from "../../hooks/useMessage";

export const Login: FC = memo(() => {
  const navigate = useNavigate();
  const { showMessage } = useMessage();
  const [loading, setLoading] = useState(false);
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");

  const onChangeUserId = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setUserId(e.target.value),
    []
  );
  const onChangePassword = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setPassword(e.target.value),
    []
  );

  //API送信関数
  const handleSubmit = async (e: FormEvent) => {
    setLoading(true);
    e.preventDefault();
    await axios
      .post("/api/login", {
        userId: userId,
        password: password,
      })
      .then((res) => {
        //ログイン
        showMessage({ title: "ログインしました", status: "info" });
        navigate(`/employeeInfoList/${userId}`);
      })
      .catch(() => {
        showMessage({ title: "ユーザーが見つかりません", status: "error" });
        setLoading(false);
      })
      .finally(() => {});
  };

  return (
    <Flex align={"center"} justify={"center"} height={"100vh"}>
      <Box bg={"white"} w={"sm"} p={4} borderRadius={"md"} shadow={"md"}>
        <Heading as={"h1"} size={"lg"} textAlign={"center"}>
          従業員プロフィール閲覧アプリ
        </Heading>
        <Divider my={4} />
        <Stack spacing={6} py={4} px={10}>
          <form onSubmit={handleSubmit}>
            <Input
              placeholder="ユーザID"
              type="text"
              value={userId}
              onChange={onChangeUserId}
            />
            <Input
              placeholder="パスワード"
              type="password"
              value={password}
              onChange={onChangePassword}
            />
            <Button
              bg={"teal.400"}
              color={"white"}
              _hover={{ opacity: 0.8 }}
              isDisabled={userId === "" || password === ""}
              isLoading={loading}
            >
              ログイン
            </Button>
          </form>
        </Stack>
      </Box>
    </Flex>
    // <div>
    //   <h1>ログイン画面</h1>
    //   {statusNgFlag && (
    //     <span style={{ color: "red" }}>
    //       ユーザIDまたはパスワードが間違っています
    //     </span>
    //   )}
    //   <form onSubmit={handleSubmit}>
    //     <input
    //       type="text"
    //       placeholder="ユーザID"
    //       name="userId"
    //       value={userId}
    //       onChange={onChangeUserId}
    //     />
    //     <br />
    //     <input
    //       type="password"
    //       placeholder="パスワード"
    //       name="password"
    //       value={password}
    //       onChange={onChangePassword}
    //     />
    //     <button type="submit">ログイン</button>
    //   </form>
    //   <Link to="/">TOPへ戻る</Link>
    // </div>
  );
});
