import { useCallback } from "react";
import { Box, Flex, Heading, Link, useDisclosure } from "@chakra-ui/react";
import { FC, memo } from "react";

import { MenuIconButton } from "../../atoms/button/MenuIconButton";
import { MenuDrawer } from "../../molecules/MenuDrawer";
import { useNavigate } from "react-router";
import { useRecoilState } from "recoil";
import { EmployeeState } from "../../../contexts/EmployeeState";

export const Header: FC = memo(() => {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [employeeState, setEmployeeState] = useRecoilState(EmployeeState);

  const navigation = useNavigate();

  const onClickHome = useCallback(() => navigation(`/home/${employeeState}`), [navigation]);
  const onClickEmployeeInfoList = useCallback(
    () => navigation(`/home/employeeInfoList/${employeeState}`),
    [navigation]
  );
  const onClickSetting = useCallback(
    () => navigation(`/home/setting/${employeeState}`),
    [navigation]
  );

  return (
    <>
      <Flex
        as="nav"
        bg="teal.500"
        color="gray.50"
        align="center"
        justify="space-between"
        padding={{ base: 3, md: 5 }}
      >
        <Flex
          align="center"
          as="a"
          mr={8}
          _hover={{ cursor: "pointer" }}
          onClick={onClickHome}
        >
          <Heading as="h1" fontSize={{ base: "md", md: "lg" }}>
            ユーザー管理アプリ
          </Heading>
        </Flex>
        <Flex
          align="center"
          fontSize="sm"
          flexGrow={2}
          display={{ base: "none", md: "flex" }}
        >
          <Box pr={4}>
            <Link onClick={onClickEmployeeInfoList}>ユーザー一覧</Link>
          </Box>
          <Link onClick={onClickSetting}>設定</Link>
        </Flex>
        <MenuIconButton onOpen={onOpen} />
      </Flex>
      <MenuDrawer
        onClose={onClose}
        isOpen={isOpen}
        onClickHome={onClickHome}
        onClickEmployeeInfoList={onClickEmployeeInfoList}
        onClickSetting={onClickSetting}
      />
    </>
  );
});
