import { FC, memo, useRef } from "react";
import {
  AlertDialog,
  AlertDialogBody,
  AlertDialogContent,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogOverlay,
  Button,
  Drawer,
  DrawerBody,
  DrawerContent,
  DrawerOverlay,
  useDisclosure,
} from "@chakra-ui/react";
import { useNavigate } from "react-router";

type Props = {
  onClose: () => void;
  isOpen: boolean;
  onClickHome: () => void;
  onClickEmployeeInfoList: () => void;
  onClickSetting: () => void;
};

export const MenuDrawer: FC<Props> = memo((props) => {
  const logoutDisclosure = useDisclosure();
  const cancelRef = useRef();
  const navigation = useNavigate();
  const {
    onClose,
    isOpen,
    onClickHome,
    onClickEmployeeInfoList,
    onClickSetting,
  } = props;
  return (
    <Drawer placement="right" size={"xs"} isOpen={isOpen} onClose={onClose}>
      <DrawerOverlay>
        <DrawerContent>
          <DrawerBody padding={0} bg={"gray.100"}>
            <Button w={"100%"} onClick={onClickHome}>
              TOP
            </Button>
            <Button w={"100%"} onClick={onClickEmployeeInfoList}>
              ユーザー一覧
            </Button>
            <Button w={"100%"} onClick={onClickSetting}>
              設定
            </Button>
            <Button w={"100%"} onClick={onClickSetting}>
              社員登録
            </Button>
            <Button w={"100%"} onClick={onClickSetting}>
              ログアウト
            </Button>
            <AlertDialog
              isOpen={logoutDisclosure.isOpen}
              leastDestructiveRef={cancelRef}
              onClose={logoutDisclosure.onClose}
            >
              <AlertDialogOverlay>
                <AlertDialogContent>
                  <AlertDialogHeader fontSize="lg" fontWeight="bold">
                    ログアウト
                  </AlertDialogHeader>

                  <AlertDialogBody>ログアウトしますか？</AlertDialogBody>

                  <AlertDialogFooter>
                    <Button ref={cancelRef} onClick={logoutDisclosure.onClose}>
                      いいえ
                    </Button>
                    <Button
                      colorScheme="red"
                      onClick={() => navigation("/")}
                      ml={3}
                    >
                      はい
                    </Button>
                  </AlertDialogFooter>
                </AlertDialogContent>
              </AlertDialogOverlay>
            </AlertDialog>
          </DrawerBody>
        </DrawerContent>
      </DrawerOverlay>
    </Drawer>
  );
});
