import axios from "axios";
import { useCallback, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useMessage } from "./useMessage";
import { useEmployee } from "./useEmployee";
import { EmployeeState, EmployeeAdminState } from "../contexts/EmployeeState";
import { useRecoilState } from "recoil";
import { AuthProvider } from "../contexts/AuthProvider";

type Props = {
  employeeId: string;
  password: string;
};

export const useLogin = () => {
  const navigate = useNavigate();
  const { showMessage } = useMessage();
  const { getEmployee, load } = useEmployee();
  const [loading, setLoading] = useState(false);
  const [employeeState, setEmployeeState] = useRecoilState(EmployeeState);
  const [authProvider, setAuthProvider] = useRecoilState(AuthProvider);
  const [employeeAdminState, setEmployeeAdminState] =
    useRecoilState(EmployeeAdminState);

  const login = useCallback(async (props: Props) => {
    const { employeeId, password } = props;
    setLoading(true);
    await axios
      .post("/api/login", {
        employeeId: employeeId,
        password: password,
      })
      .then((res) => {
        //ログイン
        setEmployeeState(employeeId);
        setEmployeeAdminState(res.data);
        showMessage({ title: "ログインしました", status: "success" });
        //ユーザ情報取得
        getEmployee({ employeeId });
        setAuthProvider(true);
        navigate("/home");
      })
      .catch((e) => {
        showMessage({ title: "ユーザーが見つかりません", status: "error" });
        setLoading(false);
        setEmployeeAdminState(1);
        setAuthProvider(true);
        navigate("/home");
      });
  }, []);

  return { login, loading };
};
