import { ChangeEvent, FC, FormEvent, memo, useCallback, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  Box,
  Button,
  Divider,
  Flex,
  Heading,
  Input,
  Stack,
} from "@chakra-ui/react";
import { useRecoilState } from "recoil";

import { useMessage } from "../../hooks/useMessage";
import { UserState } from "../../contexts/UserState";

export const Login: FC = memo(() => {
  const navigate = useNavigate();
  const { showMessage } = useMessage();
  const [loading, setLoading] = useState(false);
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");
  const [userState, setUserState] = useRecoilState(UserState);

  const onChangeUserId = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setUserId(e.target.value),
    [userId]
  );
  const onChangePassword = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setPassword(e.target.value),
    [password]
  );

  //API送信関数
  const handleSubmit = async (e:any) => {
    setLoading(true);
    e.preventDefault();
    await axios
      .post("/api/login", {
        userId: userId,
        password: password,
      })
      .then((res) => {
        if (res.status === 200) {
          //ログイン
          setUserState(userId);
          showMessage({ title: "ログインしました", status: "info" });
          navigate(`/home/${userId}`);
        } else {
          showMessage({ title: "ユーザーが見つかりません", status: "error" });
          setLoading(false);
        }
      })
      .catch(() => {
        showMessage({ title: "ユーザーが見つかりません", status: "error" });
        setLoading(false);
      })
      .finally(() => {});
  };

  return (
    <Flex align={"center"} justify={"center"} height={"100vh"}>
      <Box bg={"white"} w={"sm"} p={4} borderRadius={"md"} shadow={"md"}>
        <Heading as={"h1"} size={"lg"} textAlign={"center"}>
          従業員プロフィール閲覧アプリ
        </Heading>
        <Divider my={4} />
        <Stack spacing={6} py={10} px={10}>
          <Input
            placeholder="ユーザID"
            type="text"
            value={userId}
            name="userId"
            onChange={onChangeUserId}
          />
          <Input
            placeholder="パスワード"
            type="password"
            value={password}
            name="password"
            onChange={onChangePassword}
          />
          <Button
            bg={"teal.400"}
            color={"white"}
            _hover={{ opacity: 0.8 }}
            isDisabled={userId === "" || password === ""}
            isLoading={loading}
            onClick={handleSubmit}
            onKeyDown={handleSubmit}
          >
            ログイン
          </Button>
        </Stack>
      </Box>
    </Flex>
  );
});
