import { FC, memo, useState } from "react";
import { HeaderOnly } from "../templates/layout/HeaderOnly";
import { Box, Divider, Flex, Heading, Input, Stack } from "@chakra-ui/react";
import { PrimaryButton } from "../atoms/button/PrimaryButton";
import { useMessage } from "../../hooks/useMessage";
import { useRecoilState } from "recoil";
import { EmployeeState } from "../../contexts/EmployeeState";

export const EditProfile: FC = memo(() => {
    const { showMessage } = useMessage();
    const [loading, setLoading] = useState(false);
    const [employeeState, setEmployeeState] = useRecoilState(EmployeeState);
    const [employeeName, setEmployeeName] = useState("");
    const [employeeFullName, setEmployeeFullName] = useState("");
    const [hobbies, setHobbies] = useState("");

  

    
  return (
    <HeaderOnly>
      <Flex align={"center"} justify={"center"} height={"100vh"}>
        <Box bg={"white"} w={"sm"} p={4} borderRadius={"md"} shadow={"md"}>
          <Heading as={"h1"} size={"lg"} textAlign={"center"}>
            プロフィール編集
          </Heading>
          <Divider my={4} />
          <Stack spacing={6} py={10} px={10}>
            <Input
              placeholder="社員ID"
              type="text"
              value={employeeState}
              name="employeeId"
              readOnly={true}
            />
            <Input
              placeholder="ニックネーム"
              type="text"
              value={employeeName}
              name="employeeName"
              onChange={onChangeOldPassword}
            />
            <Input
              placeholder="フルネーム"
              type="text"
              value={employeeFullName}
              name="employeeFullName"
              onChange={onChangeNewPassword}
            />
            <Input
              placeholder="趣味"
              type="text"
              value={hobbies}
              name="hobbies"
              onChange={onChangeNewPassword}
            />
            <Flex>
              <PrimaryButton loading={loading} onClick={handleCancelButton}>
                キャンセル
              </PrimaryButton>
              <PrimaryButton loading={loading} onClick={handleUpdateButton}>
                更新
              </PrimaryButton>
            </Flex>
          </Stack>
        </Box>
      </Flex>
    </HeaderOnly>
  );
});
