import { ChangeEvent, FormEvent, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

interface FormData {
  userId: string;
  password: string;
}

export const LoginAdmin: React.FC = () => {
  const navigation = useNavigate();
  const [formData, setFormData] = useState<FormData>({
    userId: "",
    password: "",
  });
  const [text, setText] = useState("");

  // const handleChange = (event: ChangeEvent<HTMLInputElement>) =>
  //   setFormData({ ...formData, [event.target.name]: event.target.value });

  // const handleSubmit = async (event: FormEvent<HTMLFormElement>) => {
  //   event.preventDefault();
  //   try {
  //     const responce = await axios.post<FormData>("/api/posts", formData);
  //     console.log("responce:", responce.data);
  //     navigation(`/${responce.data.userId}`);

  //   } catch (e) {
  //     console.log("Error:", e);
  //   }
  // };

  const handleChange = (event: ChangeEvent<HTMLInputElement>) => setText(event.target.value);

  const handleSubmit = async (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    try {
      const responce = await axios.post("/api/posts", text);
      console.log("responce:", responce.data);
    } catch (e) {
      console.log("Error:", e);
    }
  };

  return (
    <div>
      <h1>管理者ログイン画面</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="ユーザID"
          name="userId"
          value={text}
          onChange={handleChange}
        />
        <br />
        {/* <input
          type="password"
          placeholder="パスワード"
          name="password"
          value={formData.password}
          onChange={handleChange}
        /> */}
        <button type="submit">ログイン</button>
      </form>
      <Link to="/">TOPへ戻る</Link>
    </div>
  );
};
