import { ChangeEvent, FC, memo, useCallback, useEffect, useState } from "react";
import { HeaderOnly } from "../templates/layout/HeaderOnly";
import {
  Box,
  Divider,
  Flex,
  Heading,
  Input,
  Stack,
  Image,
  ButtonGroup,
  FormLabel,
  VisuallyHiddenInput,
} from "@chakra-ui/react";
import { PrimaryButton } from "../atoms/button/PrimaryButton";
import { useEditProfile } from "../../hooks/useEditProfile";
import { useRecoilState } from "recoil";
import {
  EmployeeState,
  EmployeeImageState,
} from "../../contexts/EmployeeState";
import { useNavigate } from "react-router-dom";
import { useEmployee } from "../../hooks/useEmployee";

export const EditProfile: FC = memo(() => {
  const navigate = useNavigate();
  const { getEmployee, employee, load } = useEmployee();
  const { updateProfile, loading } = useEditProfile();
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [employeeState, setEmployeeState] = useRecoilState(EmployeeState);
  const [employeeImageState, setEmployeeImageState] =
    useRecoilState(EmployeeImageState);

  const [employeeName, setEmployeeName] = useState("");
  const [employeeFullName, setEmployeeFullName] = useState("");
  const [hobbies, setHobbies] = useState("");

  const onChangeEmployeeName = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setEmployeeName(e.target.value),
    [employeeName]
  );

  const onChangeEmployeeFullName = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setEmployeeFullName(e.target.value),
    [employeeFullName]
  );

  const onChangeHobbies = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setHobbies(e.target.value),
    [hobbies]
  );

  const handleCancelButton = () => {
    navigate("/home/setting");
  };

  //画像のプレビュー表示
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      console.log(file);
      console.log(selectedFile);
      setPreviewUrl(URL.createObjectURL(file));
    }
  };

  useEffect(() => {
    getEmployee({ employeeId: employeeState });
    console.log("戻ってきた");
    console.log(employee);
    setEmployeeName(employee.employeeName);
    setEmployeeFullName(employee.employeeFullName);
    setHobbies(employee.hobbies);
  }, []);

  return (
    <HeaderOnly>
      <Flex align={"center"} justify={"center"} height={"120vh"}>
        <Box bg={"white"} w={"sm"} p={4} borderRadius={"md"} shadow={"md"}>
          <Heading as={"h1"} size={"lg"} textAlign={"center"}>
            プロフィール編集
          </Heading>
          <Divider my={4} />
          <Stack spacing={6} py={10} px={10}>
            {previewUrl != null ? (
              <Image
                borderRadius={"full"}
                boxSize={"160px"}
                src={previewUrl}
                alt="画像"
                m={"auto"}
                bg={"gray.100"}
                textAlign={"center"}
              />
            ) : (
              <Image
                borderRadius={"full"}
                boxSize={"160px"}
                src={`data:image/jpeg;base64,${employeeImageState}`}
                alt="画像"
                m={"auto"}
                bg={"gray.100"}
                textAlign={"center"}
              />
            )}
            <FormLabel
              htmlFor="image_uploads"
              border={"solid"}
              _hover={{ cursor: "pointer", opacity: 0.8 }}
              textAlign={"center"}
            >
              アップロード画像を選択
            </FormLabel>
            <VisuallyHiddenInput
              type="file"
              id="image_uploads"
              padding={"5px"}
              accept="image/*"
              onChange={handleFileChange}
              multiple
            ></VisuallyHiddenInput>
            <Input
              placeholder="社員ID"
              type="text"
              value={employeeState}
              name="employeeId"
              readOnly={true}
            />
            <Input
              placeholder="ニックネーム"
              type="text"
              value={employeeName}
              name="employeeName"
              onChange={onChangeEmployeeName}
            />
            <Input
              placeholder="フルネーム"
              type="text"
              value={employeeFullName}
              name="employeeFullName"
              onChange={onChangeEmployeeFullName}
            />
            <Input
              placeholder="趣味"
              type="text"
              value={hobbies}
              name="hobbies"
              onChange={onChangeHobbies}
            />
            <Flex>
              <ButtonGroup gap="2" spacing="20">
                <PrimaryButton onClick={handleCancelButton}>
                  キャンセル
                </PrimaryButton>
                <PrimaryButton
                  loading={loading}
                  onClick={() =>
                    updateProfile({
                      imageFile: selectedFile,
                      employeeId: employeeState,
                      employeeName: employeeName,
                      employeeFullName: employeeFullName,
                      hobbies: hobbies,
                    })
                  }
                >
                  更新
                </PrimaryButton>
              </ButtonGroup>
            </Flex>
          </Stack>
        </Box>
      </Flex>
    </HeaderOnly>
  );
});
