import React from "react";
import { ChangeEvent, FC, FormEvent, memo, useCallback, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Box, Divider, Flex, Heading, Input, Stack, Switch } from "@chakra-ui/react";
import { useRecoilState } from "recoil";

import { useMessage } from "../../hooks/useMessage";
import {
  EmployeeState,
  EmployeeAdminState,
} from "../../contexts/EmployeeState";
import { PrimaryButton } from "../atoms/button/PrimaryButton";
import { HeaderOnly } from "../templates/layout/HeaderOnly";

export const EmployeeResister: FC = memo(() => {
  const navigate = useNavigate();
  const { showMessage } = useMessage();
  const [loading, setLoading] = useState(false);
  const [employeeId, setEmployeeId] = useState("");
  const [password, setPassword] = useState("");
  const [employeeState, setEmployeeState] = useRecoilState(EmployeeState);
  const [employeeAdminState, setEmployeeAdminState] =
    useRecoilState(EmployeeAdminState);

  const onChangeEmployeeId = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setEmployeeId(e.target.value),
    [employeeId]
  );
  const onChangePassword = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setPassword(e.target.value),
    [password]
  );

  //API送信関数
  const handleSubmit = async (e: FormEvent) => {
    setLoading(true);
    e.preventDefault();
    await axios
      .post("/api/register", {
        employeeId: employeeId,
        password: password,
      })
      .then((res) => {
        if (res.status === 200) {
          showMessage({ title: "社員登録が完了しました", status: "info" });
        } else {
          showMessage({ title: "社員登録が出来ませんでした", status: "error" });
          setLoading(false);
        }
      })
      .catch(() => {
        showMessage({ title: "社員登録が出来ませんでした", status: "error" });
        setLoading(false);
      });
  };

  return (
    <HeaderOnly>
      <Flex align={"center"} justify={"center"} height={"100vh"}>
        <Box bg={"white"} w={"sm"} p={4} borderRadius={"md"} shadow={"md"}>
          <Heading as={"h1"} size={"lg"} textAlign={"center"}>
            社員登録
          </Heading>
          <Divider my={4} />
          <Stack spacing={6} py={10} px={10}>
            <Input
              placeholder="社員ID"
              type="text"
              value={employeeId}
              name="employeeId"
              onChange={onChangeEmployeeId}
            />
            <Input
              placeholder="初期パスワード"
              type="password"
              value={password}
              name="password"
              onChange={onChangePassword}
            />
            <PrimaryButton
              disabled={employeeId === "" || password === ""}
              loading={loading}
              onClick={handleSubmit}
            >
              登録
            </PrimaryButton>
          </Stack>
        </Box>
      </Flex>
    </HeaderOnly>
  );
});
