import { FC, FormEvent, memo, useState } from "react";
import {
  Stack,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  FormControl,
  FormLabel,
  Input,
  Button,
} from "@chakra-ui/react";
import { Employee } from "../../../types/api/Employee";
import { PrimaryButton } from "../../atoms/button/PrimaryButton";
import { useNavigate } from "react-router-dom";
import { useMessage } from "../../../hooks/useMessage";
import { useRecoilState } from "recoil";
import {
  EmployeeAdminState,
  EmployeeState,
} from "../../../contexts/EmployeeState";
import axios from "axios";

type Props = {
  employee: Employee | null;
  isOpen: boolean;
  onClose: () => void;
};

export const EmployeeDetailModal: FC<Props> = memo((props) => {
  const { employee, isOpen, onClose } = props;
  const navigate = useNavigate();
  const { showMessage } = useMessage();
  const [loading, setLoading] = useState(false);
  const [employeeState, setEmployeeState] = useRecoilState(EmployeeState);

  //API送信関数
  const handleSubmit = async (e: FormEvent) => {
    setLoading(true);
    e.preventDefault();
    await axios
      .post("/api/delete", {
        employeeId: employeeState,
      })
      .then((res) => {
        if (res.status === 200) {
          showMessage({ title: "削除が完了しました", status: "info" });
          setLoading(false);
          onClose;
        } else {
          showMessage({ title: "削除が出来ませんでした", status: "error" });
          setLoading(false);
        }
      })
      .catch(() => {
        showMessage({ title: "削除が出来ませんでした", status: "error" });
        setLoading(false);
      });
  };

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      autoFocus={false}
      motionPreset="slideInBottom"
    >
      <ModalOverlay>
        <ModalContent padding={6}>
          <ModalHeader>従業員詳細</ModalHeader>
          <ModalCloseButton />
          <ModalBody mx={4}>
            <Stack spacing={4}>
              <FormControl>
                <FormLabel>名前</FormLabel>
                <Input value={employee?.employeeName} isReadOnly />
                <FormControl>
                  <FormLabel>フルネーム</FormLabel>
                  <Input value={employee?.employeeFullName} isReadOnly />
                </FormControl>
                <FormControl>
                  <FormLabel>趣味</FormLabel>
                  <Input value={employee?.hobbies} isReadOnly />
                </FormControl>
              </FormControl>
            </Stack>
            <PrimaryButton children={"削除"} onClick={handleSubmit} />
          </ModalBody>
        </ModalContent>
      </ModalOverlay>
    </Modal>
  );
});
