import { FC, FormEvent, memo, useState } from "react";
import {
  Stack,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  FormControl,
  FormLabel,
  Input,
} from "@chakra-ui/react";
import { Employee } from "../../../types/api/Employee";
import { PrimaryButton } from "../../atoms/button/PrimaryButton";
import { useNavigate } from "react-router-dom";
import { useMessage } from "../../../hooks/useMessage";
import axios from "axios";
import { useAllEmployees } from "../../../hooks/useAllEmployees";

type Props = {
  employee: Employee | null;
  isOpen: boolean;
  onClose: () => void;
  id: string;
  isAdmin: number;
};

export const EmployeeDetailModal: FC<Props> = memo((props) => {
  const { employee, isOpen, onClose, id, isAdmin } = props;
  const navigate = useNavigate();
  const { showMessage } = useMessage();
  const [loading, setLoading] = useState(false);

  //API送信関数
  const handleSubmit = async (e: FormEvent) => {
    setLoading(true);
    e.preventDefault();
    await axios
      .post("/api/delete", {
        employeeId: employee.employeeId,
      })
      .then((res) => {
        if (res.status === 200) {
          showMessage({ title: "削除が完了しました", status: "info" });
          navigate("/home");
        } else {
          showMessage({ title: "削除が出来ませんでした", status: "error" });
          setLoading(false);
        }
      })
      .catch(() => {
        showMessage({ title: "削除が出来ませんでした", status: "error" });
        setLoading(false);
      });
  };

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      autoFocus={false}
      motionPreset="slideInBottom"
    >
      <ModalOverlay>
        <ModalContent padding={6}>
          <ModalHeader>従業員詳細</ModalHeader>
          <ModalCloseButton />
          <ModalBody mx={4}>
            <Stack spacing={4}>
              <FormControl>
                <FormLabel>名前</FormLabel>
                <Input value={employee?.employeeName} isReadOnly />
                <FormControl>
                  <FormLabel>フルネーム</FormLabel>
                  <Input value={employee?.employeeFullName} isReadOnly />
                </FormControl>
                <FormControl>
                  <FormLabel>趣味</FormLabel>
                  <Input value={employee?.hobbies} isReadOnly />
                </FormControl>
              </FormControl>
            </Stack>
            {isAdmin === 1 && id !== employee?.employeeId ? (
              <PrimaryButton children={"削除"} onClick={handleSubmit} />
            ) : null}
          </ModalBody>
        </ModalContent>
      </ModalOverlay>
    </Modal>
  );
});
