import {
  ChangeEvent,
  FC,
  FormEvent,
  memo,
  useCallback,
  useEffect,
  useState,
} from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Box, Divider, Flex, Heading, Input, Stack } from "@chakra-ui/react";
import { useRecoilState } from "recoil";

import { useMessage } from "../../hooks/useMessage";
import { useEmployee } from "../../hooks/useEmployee";
import {
  EmployeeState,
  EmployeeAdminState,
  EmployeeNameState,
  EmployeeFullNameState,
  EmployeeHobbiesState,
  EmployeeImageState,
} from "../../contexts/EmployeeState";
import { PrimaryButton } from "../atoms/button/PrimaryButton";

export const Login: FC = memo(() => {
  const navigate = useNavigate();
  const { showMessage } = useMessage();
  const { getEmployee, employee, loading } = useEmployee();
  const [load, setLoad] = useState(false);
  const [employeeId, setEmployeeId] = useState("");
  const [password, setPassword] = useState("");
  const [employeeState, setEmployeeState] = useRecoilState(EmployeeState);
  const [employeeAdminState, setEmployeeAdminState] =
    useRecoilState(EmployeeAdminState);
  const [employeeNameState, setEmployeeNameState] =
    useRecoilState(EmployeeNameState);
  const [employeeFullNameState, setEmployeeFullNameState] = useRecoilState(
    EmployeeFullNameState
  );
  const [employeeHobbiesState, setEmployeeHobbiesState] =
    useRecoilState(EmployeeHobbiesState);
  const [employeeImageState, setEmployeeImageState] =
    useRecoilState(EmployeeImageState);

  const onChangeEmployeeId = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setEmployeeId(e.target.value),
    [employeeId]
  );
  const onChangePassword = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setPassword(e.target.value),
    [password]
  );

  //API送信関数
  const handleSubmit = async (e: FormEvent) => {
    setLoad(true);
    e.preventDefault();
    await axios
      .post("/api/login", {
        employeeId: employeeId,
        password: password,
      })
      .then((res) => {
        console.log(res)
        if (res.status === 200) {
          //ログイン
          setEmployeeState(employeeId);
          setEmployeeAdminState(res.data);
          showMessage({ title: "ログインしました", status: "info" });
          console.log("1")
          //ユーザ情報取得
          getEmployee({employeeId});
          console.log("2")
          setEmployeeNameState(employee.employeeName);
          console.log("3")
          setEmployeeFullNameState(employee.employeeFullName);
          console.log("4")
          setEmployeeHobbiesState(employee.hobbies);
          console.log("5")
          setEmployeeImageState(employee.image);
          console.log("6")
          navigate("/home");
        } else {
          console.log("こっちでエラーが発生しました")
          showMessage({ title: "ユーザーが見つかりません", status: "error" });
          setLoad(false);
        }
      })
      .catch((e) => {
        console.log(e)
        console.log("ここでエラーが発生しました")
        showMessage({ title: "ユーザーが見つかりません", status: "error" });
        setLoad(false);
        setEmployeeAdminState(1);
        navigate("/home");
      });
  };

  return (
    <Flex align={"center"} justify={"center"} height={"100vh"}>
      <Box bg={"white"} w={"sm"} p={4} borderRadius={"md"} shadow={"md"}>
        <Heading as={"h1"} size={"lg"} textAlign={"center"}>
          社員プロフィール閲覧アプリ
        </Heading>
        <Divider my={4} />
        <Stack spacing={6} py={10} px={10}>
          <Input
            placeholder="社員ID"
            type="text"
            value={employeeId}
            name="employeeId"
            onChange={onChangeEmployeeId}
          />
          <Input
            placeholder="パスワード"
            type="password"
            value={password}
            name="password"
            onChange={onChangePassword}
          />
          <PrimaryButton
            disabled={employeeId === "" || password === ""}
            loading={loading}
            onClick={handleSubmit}
          >
            ログイン
          </PrimaryButton>
        </Stack>
      </Box>
    </Flex>
  );
});
