import { ChangeEvent, FC, FormEvent, memo, useCallback, useState } from "react";
import { HeaderOnly } from "../templates/layout/HeaderOnly";
import {
  Box,
  Divider,
  Flex,
  Heading,
  Input,
  Stack,
  Image,
  ButtonGroup,
  Button,
  TagLabel,
  FormLabel,
  VisuallyHiddenInput,
} from "@chakra-ui/react";
import { PrimaryButton } from "../atoms/button/PrimaryButton";
import { useMessage } from "../../hooks/useMessage";
import { useEditProfile } from "../../hooks/useEditProfile";
import { useRecoilState } from "recoil";
import {
  EmployeeState,
  EmployeeNameState,
  EmployeeFullNameState,
  EmployeeHobbiesState,
  EmployeeImageState,
} from "../../contexts/EmployeeState";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export const EditProfile: FC = memo(() => {
  const navigate = useNavigate();
  const { updateProfile, loading } = useEditProfile();
  const { showMessage } = useMessage();
  const [load, setLoad] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [employeeState, setEmployeeState] = useRecoilState(EmployeeState);
  const [employeeNameState, setEmployeeNameState] =
    useRecoilState(EmployeeNameState);
  const [employeeFullNameState, setEmployeeFullNameState] = useRecoilState(
    EmployeeFullNameState
  );
  const [employeeHobbiesState, setEmployeeHobbiesState] =
    useRecoilState(EmployeeHobbiesState);
  const [employeeImageState, setEmployeeImageState] =
    useRecoilState(EmployeeImageState);

  const onChangeEmployeeName = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setEmployeeNameState(e.target.value),
    [employeeNameState]
  );

  const onChangeEmployeeFullName = useCallback(
    (e: ChangeEvent<HTMLInputElement>) =>
      setEmployeeFullNameState(e.target.value),
    [employeeFullNameState]
  );

  const onChangeHobbies = useCallback(
    (e: ChangeEvent<HTMLInputElement>) =>
      setEmployeeHobbiesState(e.target.value),
    [employeeHobbiesState]
  );

  const handleCancelButton = () => {
    navigate("/home/setting");
  };

  //画像のプレビュー表示
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setPreviewUrl(URL.createObjectURL(file));
    }
  };

  //プロフィール更新
  const handleUpdateProfile = async () => {
    const formData = new FormData();
    formData.append("image", selectedFile);
    formData.append("employeeId", employeeState);
    formData.append("employeeName", employeeNameState);
    formData.append("employeeFullName", employeeFullNameState);
    formData.append("hobbies", employeeHobbiesState);

    console.log(formData);
    console.log("プロフィール更新開始");
    await axios
      .post("/api/editProfile", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
      .then((res) => {
        console.log(res.data);
        setEmployeeImageState(res.data.image)
        showMessage({title:"更新が完了しました", status:"success"})
        navigate("/home/setting")
      })
      .catch(() => {
        showMessage({title:"更新に失敗しました", status:"error"})
      });
  };

  return (
    <HeaderOnly>
      <Flex align={"center"} justify={"center"} height={"120vh"}>
        <Box bg={"white"} w={"sm"} p={4} borderRadius={"md"} shadow={"md"}>
          <Heading as={"h1"} size={"lg"} textAlign={"center"}>
            プロフィール編集
          </Heading>
          <Divider my={4} />
          <Stack spacing={6} py={10} px={10}>
            {previewUrl != null ? (
              <Image
                borderRadius={"full"}
                boxSize={"160px"}
                src={previewUrl}
                alt="画像"
                m={"auto"}
                bg={"gray.100"}
                textAlign={"center"}
              />
            ) : (
              <Image
                borderRadius={"full"}
                boxSize={"160px"}
                src={`data:image/jpeg;base64,${employeeImageState}`}
                alt="画像"
                m={"auto"}
                bg={"gray.100"}
                textAlign={"center"}
              />
            )}
            <FormLabel
              htmlFor="image_uploads"
              border={"solid"}
              _hover={{ cursor: "pointer", opacity: 0.8 }}
              textAlign={"center"}
            >
              アップロード画像を選択
            </FormLabel>
            <VisuallyHiddenInput
              type="file"
              id="image_uploads"
              padding={"5px"}
              accept="image/*"
              onChange={handleFileChange}
              multiple
            ></VisuallyHiddenInput>
            <Input
              placeholder="社員ID"
              type="text"
              value={employeeState}
              name="employeeId"
              readOnly={true}
            />
            <Input
              placeholder="ニックネーム"
              type="text"
              value={employeeNameState}
              name="employeeName"
              onChange={onChangeEmployeeName}
            />
            <Input
              placeholder="フルネーム"
              type="text"
              value={employeeFullNameState}
              name="employeeFullName"
              onChange={onChangeEmployeeFullName}
            />
            <Input
              placeholder="趣味"
              type="text"
              value={employeeHobbiesState}
              name="hobbies"
              onChange={onChangeHobbies}
            />
            <Flex>
              <ButtonGroup gap="2" spacing="20">
                <PrimaryButton onClick={handleCancelButton}>
                  キャンセル
                </PrimaryButton>
                <PrimaryButton loading={loading} onClick={handleUpdateProfile}>
                  更新
                </PrimaryButton>
              </ButtonGroup>
            </Flex>
          </Stack>
        </Box>
      </Flex>
    </HeaderOnly>
  );
});
