import { FC, FormEvent, memo, useState } from "react";
import {
  Stack,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  FormControl,
  FormLabel,
  Input,
  Box,
} from "@chakra-ui/react";
import { Employee } from "../../../types/api/Employee";
import { PrimaryButton } from "../../atoms/button/PrimaryButton";
import { useEmployeeDelete } from "../../../hooks/useEmployeeDelete";

type Props = {
  employee: Employee | null;
  isOpen: boolean;
  onClose: () => void;
  id: string;
  isAdmin: number;
};

export const EmployeeDetailModal: FC<Props> = memo((props) => {
  const { employee, isOpen, onClose, id, isAdmin } = props;
  const { employeeDelete, loading } = useEmployeeDelete();

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      autoFocus={false}
      motionPreset="slideInBottom"
    >
      <ModalOverlay>
        <ModalContent padding={6}>
          <ModalHeader>社員詳細</ModalHeader>
          <ModalCloseButton />
          <ModalBody mx={4}>
            <Stack spacing={4}>
              <FormControl>
                <FormLabel>ニックネーム</FormLabel>
                <Input value={employee?.employeeName} isReadOnly />
                <FormControl>
                  <FormLabel>フルネーム</FormLabel>
                  <Input value={employee?.employeeFullName} isReadOnly />
                </FormControl>
                <FormControl>
                  <FormLabel>趣味</FormLabel>
                  <Input value={employee?.hobbies} isReadOnly />
                </FormControl>
              </FormControl>
            </Stack>
            {isAdmin === 1 && id !== employee?.employeeId ? (
              <PrimaryButton
                children={"削除"}
                loading={loading}
                onClick={() => {
                  employeeDelete({ employeeId: employee?.employeeId });
                }}
              />
            ) : null}
          </ModalBody>
        </ModalContent>
      </ModalOverlay>
    </Modal>
  );
});
