import { ChangeEvent, FC, FormEvent, memo, useCallback, useState } from "react";
import { Form, useNavigate } from "react-router-dom";
import axios from "axios";
import { Box, Button, Flex, Heading, Input, Stack } from "@chakra-ui/react";

export const Login: FC = memo(() => {
  const navigate = useNavigate();
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");

  const onChangeUserId = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setUserId(e.target.value),
    []
  );
  const onChangePassword = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => setPassword(e.target.value),
    []
  );

  //API送信関数
  const handleSubmit = async (e: FormEvent) => {
    e.preventDefault();
    await axios
      .post("/api/login", {
        userId: userId,
        password: password,
      })
      .then((res) => {
        //ログイン
        navigate(`/employeeInfoList/${userId}`);
      })
      .catch(() => {})
      .finally(() => {});
  };

  return (
    <Flex align={"center"} justify={"center"} height={"100vh"}>
      <Box>
        <Heading>従業員プロフィール閲覧アプリ</Heading>
        <Stack>
          <form onSubmit={handleSubmit}>
            <Input
              placeholder="ユーザID"
              type="text"
              value={userId}
              onChange={onChangeUserId}
            />
            <Input
              placeholder="パスワード"
              type="password"
              value={password}
              onChange={onChangePassword}
            />
            <Button>ログイン</Button>
          </form>
        </Stack>
      </Box>
    </Flex>
    // <div>
    //   <h1>ログイン画面</h1>
    //   {statusNgFlag && (
    //     <span style={{ color: "red" }}>
    //       ユーザIDまたはパスワードが間違っています
    //     </span>
    //   )}
    //   <form onSubmit={handleSubmit}>
    //     <input
    //       type="text"
    //       placeholder="ユーザID"
    //       name="userId"
    //       value={userId}
    //       onChange={onChangeUserId}
    //     />
    //     <br />
    //     <input
    //       type="password"
    //       placeholder="パスワード"
    //       name="password"
    //       value={password}
    //       onChange={onChangePassword}
    //     />
    //     <button type="submit">ログイン</button>
    //   </form>
    //   <Link to="/">TOPへ戻る</Link>
    // </div>
  );
});
