import { FC, memo, useRef } from "react";
import {
  AlertDialog,
  AlertDialogBody,
  AlertDialogContent,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogOverlay,
  Button,
  Drawer,
  DrawerBody,
  DrawerContent,
  DrawerOverlay,
  useDisclosure,
} from "@chakra-ui/react";
import { useNavigate } from "react-router";

type Props = {
  onClose: () => void;
  isOpen: boolean;
  onClickHome: () => void;
  onClickEmployeeInfoList: () => void;
  onClickSetting: () => void;
  onClickEmployeeResister: () => void;
  onclickLogout: () => void;
};

export const MenuDrawer: FC<Props> = memo((props) => {
  const logoutDisclosure = useDisclosure();
  const cancelRef = useRef();
  const navigation = useNavigate();
  const {
    onClose,
    isOpen,
    onClickHome,
    onClickEmployeeInfoList,
    onClickSetting,
    onClickEmployeeResister,
    onclickLogout,
  } = props;
  return (
    <Drawer placement="right" size={"xs"} isOpen={isOpen} onClose={onClose}>
      <DrawerOverlay>
        <DrawerContent>
          <DrawerBody padding={0} bg={"gray.100"}>
            <Button w={"100%"} onClick={onClickHome}>
              TOP
            </Button>
            <Button w={"100%"} onClick={onClickEmployeeInfoList}>
              ユーザー一覧
            </Button>
            <Button w={"100%"} onClick={onClickSetting}>
              設定
            </Button>
            <Button w={"100%"} onClick={onClickEmployeeResister}>
              社員登録
            </Button>
            <Button w={"100%"} onClick={onclickLogout}>
              ログアウト
            </Button>
          </DrawerBody>
        </DrawerContent>
      </DrawerOverlay>
    </Drawer>
  );
});
