import axios from "axios";
import { useCallback, useState } from "react";
import { Employee } from "../types/api/Employee";
import { useMessage } from "./useMessage";
import { EmployeeState } from "../contexts/EmployeeState";
import { useRecoilState } from "recoil";

type Props = {
  employeeId: string;
};

export const useEmployee = () => {
  const { showMessage } = useMessage();
  const [loading, setLoading] = useState(false);
  const [employee, setEmployee] = useState<Employee>();

  const getEmployee = useCallback(async (props: Props) => {
    const { employeeId } = props;
    console.log("従業員情報取得開始");
    setLoading(true);
    await axios
      .get("/api/employee", {
        params: {
          id: employeeId,
        },
      })
      .then((res) => {
        console.log(res);
        setEmployee(res.data);
      })
      .catch((e) => {
        console.log(e);
        showMessage({
          title: "従業員情報の取得に失敗しました",
          status: "error",
        });
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  return { getEmployee, employee, loading };
};
