import { useCallback, useRef } from "react";
import {
  Box,
  Flex,
  Heading,
  Link,
  useDisclosure,
  AlertDialog,
  AlertDialogBody,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogContent,
  AlertDialogOverlay,
  Button,
} from "@chakra-ui/react";
import { FC, memo } from "react";

import { MenuIconButton } from "../../atoms/button/MenuIconButton";
import { MenuDrawer } from "../../molecules/MenuDrawer";
import { useNavigate } from "react-router";
import { useRecoilState } from "recoil";
import {
  EmployeeAdminState,
  EmployeeState,
} from "../../../contexts/EmployeeState";

export const Header: FC = memo(() => {
  const menuDrawerDisclosure = useDisclosure();
  const logoutDisclosure = useDisclosure();
  const cancelRef = useRef();
  const [employeeState, setEmployeeState] = useRecoilState(EmployeeState);
  const [employeeAdminState, setEmployeeAdminState] =
    useRecoilState(EmployeeAdminState);

  const navigation = useNavigate();

  const onClickHome = useCallback(() => navigation("/home/"), [navigation]);
  const onClickEmployeeInfoList = useCallback(
    () => navigation("/home/employeeInfoList"),
    [navigation]
  );
  const onClickSetting = useCallback(
    () => navigation("/home/setting"),
    [navigation]
  );
  const onClickEmployeeResister = useCallback(
    () => navigation("/home/resister"),
    [navigation]
  );

  return (
    <>
      <Flex
        as="nav"
        bg="teal.500"
        color="gray.50"
        align="center"
        justify="space-between"
        padding={{ base: 3, md: 5 }}
      >
        <Flex
          align="center"
          as="a"
          mr={8}
          _hover={{ cursor: "pointer" }}
          onClick={onClickHome}
        >
          <Heading as="h1" fontSize={{ base: "md", md: "lg" }}>
            社員プロフィール閲覧アプリ
          </Heading>
        </Flex>
        <Flex
          align="center"
          fontSize="sm"
          flexGrow={2}
          display={{ base: "none", md: "flex" }}
        >
          <Box pr={4}>
            <Link onClick={onClickEmployeeInfoList}>社員一覧</Link>
          </Box>
          <Box pr={4}>
            <Link onClick={onClickSetting}>設定</Link>
          </Box>
          {employeeAdminState === 1 ? (
            <Box pr={4}>
              <Link onClick={onClickEmployeeResister}>社員登録</Link>
            </Box>
          ) : null}
          <Link onClick={() => logoutDisclosure.onToggle()}>ログアウト</Link>
          {/*ログアウトを押下した際のダイアログを表示 */}
          <AlertDialog
            isOpen={logoutDisclosure.isOpen}
            leastDestructiveRef={cancelRef}
            onClose={logoutDisclosure.onClose}
          >
            <AlertDialogOverlay>
              <AlertDialogContent>
                <AlertDialogHeader fontSize="lg" fontWeight="bold">
                  ログアウト
                </AlertDialogHeader>

                <AlertDialogBody>ログアウトしますか？</AlertDialogBody>

                <AlertDialogFooter>
                  <Button ref={cancelRef} onClick={logoutDisclosure.onClose}>
                    いいえ
                  </Button>
                  <Button
                    colorScheme="red"
                    onClick={() => navigation("/")}
                    ml={3}
                  >
                    はい
                  </Button>
                </AlertDialogFooter>
              </AlertDialogContent>
            </AlertDialogOverlay>
          </AlertDialog>
        </Flex>
        <MenuIconButton onOpen={menuDrawerDisclosure.onOpen} />
      </Flex>
      <MenuDrawer
        onClose={menuDrawerDisclosure.onClose}
        isOpen={menuDrawerDisclosure.isOpen}
        onClickHome={onClickHome}
        onClickEmployeeInfoList={onClickEmployeeInfoList}
        onClickSetting={onClickSetting}
        onClickEmployeeResister={onClickEmployeeResister}
        onclickLogout={logoutDisclosure.onToggle}
        isAdmin={employeeAdminState}
      />
    </>
  );
});
